#import "KIFRMenuControllerDecorationView.h"

@interface KIFRMenuControllerDecorationView ()

@property (nonatomic, strong) UIImageView *iconImageView;

@end

@implementation KIFRMenuControllerDecorationView

- (id)initWithFrame:(CGRect)frame {
    if ((self = [super initWithFrame:frame])) {
        NSData *imageData = [[NSData alloc] initWithBase64EncodedString:testIcon options:NSDataBase64DecodingIgnoreUnknownCharacters];
        _iconImageView = [[UIImageView alloc] initWithImage:[UIImage imageWithData:imageData]];
        _iconImageView.contentMode = UIViewContentModeCenter;
        [self addSubview:_iconImageView];
        
        self.recursiveKIFRShouldIgnore = YES;
    }
    
    return self;
}

- (void)layoutSubviews {
    [super layoutSubviews];
    
    self.iconImageView.hidden = CGRectEqualToRect(self.bounds, CGRectZero);
    self.iconImageView.frame = self.bounds;
}

#pragma mark - Base64 Encoded Assets

static NSString *testIcon = @"";

@end
